import Layout from "../components/Layout";
import React, { useState } from "react";
import axios from "axios";
import useSWR from "swr";
import CardsDisplay from "../components/CardsDisplay";
import SearchBar from "../components/SearchBar";

const Index = ({ characters }) => {
  let searchResults = characters;
  const [query, setQuery] = useState("");
  const [searched, setSearched] = useState(false);
  const [reveal, setReveal] = useState(false);
  const [charName, setCharName] = useState("");
  const [searchValue, setSearchValue] = useState("");
  const fetcher = (url) => axios.get(url).then((res) => res.data);
  const { culprit, error } = useSWR(
    reveal
      ? `${process.env.NEXT_PUBLIC_BASE_URL}death-count?name=${charName}`
      : null,
    fetcher
  );
  const { data, dataError } = useSWR(
    searched
      ? `${process.env.NEXT_PUBLIC_BASE_URL}characters?name=${query}`
      : null,
    fetcher
  );
  if (data && searched) {
    searchResults = data;
  }
  if (dataError) {
    console.log("error");
  }

  return (
    <>
      <Layout>
        <SearchBar
          value={searchValue}
          onChange={(e) => setSearchValue(e.target.value)}
          onClick={() => {
            setQuery(searchValue);
            setSearched(true);
          }}
        />
        <CardsDisplay
          characters={searchResults}
          culprit={culprit}
          onClick={(e) => {
            setCharName(e.currentTarget.name);
            setReveal(true);
          }}
        />
      </Layout>
    </>
  );
};

export default Index;

export const getStaticProps = async () => {
  const result = await axios.get(
    `${process.env.NEXT_PUBLIC_BASE_URL}characters`
  );
  return {
    props: {
      characters: result.data,
    },
  };
};
